import { Command } from 'commander';

/**
 * Get the path to the project's .env file.
 * @returns The path to the .env file
 */
declare function getGlobalEnvPath(): Promise<string>;
/**
 * Parse an .env file and return the key-value pairs
 * @param filePath Path to the .env file
 * @returns Object containing the key-value pairs
 */
declare function parseEnvFile(filePath: string): Promise<Record<string, string>>;
declare const env: Command;

export { env, getGlobalEnvPath, parseEnvFile };
