import { Command } from 'commander';

/** Reads and parses package.json, returning dependencies. */
declare const readPackageJson: (cwd: string) => {
    dependencies: Record<string, string>;
    devDependencies: Record<string, string>;
    allDependencies: Record<string, string>;
} | null;
/**
 * Normalizes a plugins input string to a standard format, typically 'plugin-name'.
 * Used primarily for display and generating commands in npx instructions.
 */
declare const normalizePluginNameForDisplay: (pluginInput: string) => string;
/** Finds the actual package name in dependencies based on various input formats. */
declare const findPluginPackageName: (pluginInput: string, allDependencies: Record<string, string>) => string | null;
declare const plugins: Command;
declare const pluginsCommand: Command;

export { findPluginPackageName, normalizePluginNameForDisplay, plugins, pluginsCommand, readPackageJson };
